mapper.select:
SELECT

FROM wm_file wm



        AND wm.file_name like CONCAT('%',#{dto.fileName},'%')


        AND wu.name like CONCAT(#{dto.author},'%')


        AND wm.channel_id = #{dto.channelId}


        AND wm.created_time > #{dto.beginDate}


        AND wm.created_time < #{dto.endDate}


order by wm.created_time desc
limit #{offset},#{size}
